  if (arg.task == 'read') {
    let name = arg.data.name;
    let data = arg.data.data;
    if (name == 'budget') {
      budget = data;
      startup('month');

    } else if (name == 'month') {
      month = data;
      startup('incomes');
      startup('outcomes');

    } else if (name == 'incomes') {
      incomes = data;

    } else if (name == 'outcomes') {
      outcomes = data;

    } else if (name == 'config') {
      config = data;
      startup('budget');

    } else {
      console.log('invalid name');
    }
  }
}

