/**
 * @ignore
 */
const appRoot = path.dirname(require.main.filename);

if (platform == 'win32') {
  var tempPath = os.homedir() + '\\.YourHomeBudget\\';
} else {
  var tempPath = os.homedir() + '/.YourHomeBudget/';
}
/**
 * @ignore
 */
const filesPath = tempPath;

/**
 * @ignore
 */
function progDir() {
  directory(filesPath);
}

/**
 * Checks if main program directory exists, if it doesn't it creates it (backend window)
 * @param {string} path Path to directory
 */
function directory(path) {
  if (!fs.existsSync(path)) {
    console.log('creating folder');
    fs.mkdirsSync(path);
    return false;
  }

  console.log('folder already exists');
  return true;
}

/**
 * Get file content (backend window)
 * @param {string} relPath Relative path to parent directory of the file
 * @param {string} name Name of file
 * @return {string} content of loaded file
 */
function read(relPath, name) {
  progDir();
  let path = filesPath + relPath;
  let appRootPath = `${appRoot}/${relPath}`;
  if (fs.existsSync(`${path}/${name}`)) {
    console.log(`File location: ${path}`);
    return fs.readFileSync(`${path}/${name}`, 'utf8');
  } else if (fs.existsSync(`${appRootPath}/${name}`)) {
    console.log(`File location: ${appRootPath}`);
    return (fs.readFileSync(`${appRootPath}/${name}`, 'utf8'));
  } else if (name == 'config.json' && !fs.existsSync(`${path}/${name}`)) {
    send('firstStartup');
  } else {
    console.log('File does not exist');
    return '';
  }
}

/**
 * Save informations to filesPath (backend window)
 * @param {string} relPath Relative path to parent directory of the file
 * @param {string} data Content which will be saved to file
 * @param {string} name Name of file
 */
function write(relPath, data, name) {
  progDir();
  if (typeof data == 'object') {
    data = jsonParser(false, data);
  }

  let path = filesPath + relPath;
  directory(filesPath);
  directory(path);
  fs.writeFileSync(path + name, data, 'utf8');
}

/*jshint esversion: 6 */

/**
 * Checks argument sended from backend (backend window)
 * @param {object} arg argument to check
 */
function checkArg(arg) {
  let task = arg.task;
  if (task == 'save') {
    write(arg.path, jsonParser(false, arg.data), arg.name);
    send({ done: 'saved', to: arg.path });
  } else if (task == 'read') {
    send({ task: 'read', data: jsonParser(true, read(arg.path, arg.name)) });
  } else {
    return 'invalid task';
  }

}

/**
 *Parse or stringify JSON
 * @param {boolean} mode If it's true, it will parse JSON to Object or Array, if it's false it will stringify Object or Array to JSON
 * @return  if mode is set to true {object}, if mode is set to false {string}
 */
function jsonParser(mode, arg) { //true parse, false stringify
  if (mode == true) {
    if ((typeof arg == 'string') && (arg.slice(0, 1) == '{' || arg.slice(0, 1) == '[')) {
      arg = JSON.parse(arg);
      console.log('mode: true');
      console.log(arg);
    }
  } else if (mode == false) {
    if (typeof arg == 'object') {
      arg = JSON.stringify(arg, null, '\t');
      console.log('mode: false');
    }
  } else {
    arg = null;
    console.log('invalid mode');
  }

  return arg;
}

